//         .map(index => ({ name: ['2 + 2', '3 - 2', '25 / 5', '4 * 4']}))),
//         2000
//     );
// });

const getExpressions = (expressionsCount) => new Promise((onSuccess) => {
    setTimeout(
        () => onSuccess(Array(['2 + 2', '3 - 1', '25 ÷ 5', '4 * 4'])),
        2000
    );
});

