import React from 'react';
import Button from '@material-ui/core/Button';
import { withStyles } from '@material-ui/core/styles';
//import ReactDOM from 'react-dom/client';


const RegularButton = withStyles({
  root: {
    outline: 'none',
    border: 'none',
    padding: '5%',
    width: 60,
    display: 'flex',
    margin: '2%',
    cursor: 'pointer',
    backgroundColor: '#313131',
    color: '#fff',
    fontWeight: 500,
    fontSize: '1.5rem',
    height: 62,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: '50%',
    '&:hover': {
      backgroundColor: '#635f5f',
    },
  },
  hover: {},
})(Button);

class App extends React.Component {

  constructor(props){
    super(props)
    this.state ={
      text:"0",
    }

    this.InputValue = this.InputValue.bind(this)
  }

  render(){
    return (
      <div className="container">
            <textarea className="txtarea" cols="30" rows="10">history</textarea>
            <textarea className="txt" cols="30" rows="10" value={this.state.text}></textarea>
            <div className="buttons">
              <div className="row">
                  <RegularButton hover variant='contained' onClick={this.InputValue}>7</RegularButton>
                  <RegularButton variant='contained' onClick={this.InputValue}>8</RegularButton>
                  <RegularButton variant='contained' onClick={this.InputValue}>9</RegularButton>
                  <RegularButton variant='contained' style={{ backgroundColor: '#f69906',}} onClick={this.InputValue} className="operator">+</RegularButton>
              </div>
              <div className="row">
                  <RegularButton variant='contained' onClick={this.InputValue}>4</RegularButton>
                  <RegularButton variant='contained' onClick={this.InputValue}>5</RegularButton>
                  <RegularButton variant='contained' onClick={this.InputValue}>6</RegularButton>
                  <RegularButton variant='contained' onClick={this.InputValue} style={{ backgroundColor: '#f69906',}}>-</RegularButton>
              </div>
              <div className="row">
                  <RegularButton variant='contained' onClick={this.InputValue}>1</RegularButton>
                  <RegularButton variant='contained' onClick={this.InputValue}>2</RegularButton>
                  <RegularButton variant='contained' onClick={this.InputValue}>3</RegularButton>
                  <RegularButton 
                    variant='contained' 
                    onClick={this.InputValue}
                    style={
                      { 
                        backgroundColor: '#f69906',
                        hover: {backgroundColor: "red"}
                      }
                    }

                  >
                    x
                  </RegularButton>
              </div>
              <div className="row">
                  <RegularButton variant='contained' onClick={this.InputValue} style={{ width: 137, borderRadius: 135,}}>0</RegularButton>
                  <RegularButton variant='contained' onClick={this.Calculate} style={{ backgroundColor: '#f63a06',}}>=</RegularButton>
                  <RegularButton variant='contained' onClick={this.InputValue} style={{ backgroundColor: '#f69906',}}>รท</RegularButton>
              </div>
            </div>
      </div>
    )
  }

  InputValue(event){
    if(this.state.text === '0') {
      this.setState({ text: event.target.textContent})
    }
    else {
      this.setState({ text: this.state.text + event.target.textContent})
    }

    //console.log(this.state.text)
  }

  Calculate(){

  }


}

export default App;
