import React from 'react';
import Button from '@material-ui/core/Button';
//import ReactDOM from 'react-dom/client';


class App extends React.Component {
  constructor(props){
    super(props)
    this.state ={
      text:"0",
    }

    this.InputValue = this.InputValue.bind(this)
  }

  render(){
    return (
      <div className="container">
            <textarea className="txtarea" cols="30" rows="10">history</textarea>
            <textarea className="txt" cols="30" rows="10" value={this.state.text}></textarea>
            <Button variant='contained' color='primary'></Button>
            <div className="buttons">
              <div className="row">
                  <Button variant='contained' color='primary' value="7" className='button'>7</Button>
                  <Button variant='contained' value="8"/>
                  <Button variant='contained' value="9"/>
                  <Button variant='contained' value="+" onClick={this.InputValue} className="operator"/>
              </div>
              <div className="row">
                  <Button value="4"/>
                  <Button value="5"/>
                  <Button value="6"/>
                  <Button value="-" className="operator"/>
              </div>
              <div className="row">
                  <Button value="1"/>
                  <Button value="2"/>
                  <Button value="3"/>
                  <Button value="x" className="operator"/>
              </div>
              <div className="row">
                  <Button value="0" className="zero"/>
                  <Button value="=" className="equals"/>
                  <Button value="รท" className="operator"/>
              </div>
            </div>
      </div>
    )
  }

  InputValue(value){
    this.setState({ text: this.state.text + value})
    //console.log(event.target.textContent)
    //console.log(event.target.dataset.value)
    console.log(value)
  }


}

export default App;
